import React, { Component } from 'react'
import axios from 'axios';

export default class Register extends Component {

  userData;
  constructor(props)
   {
    super(props);
    this.state = {
      signupData: {
        first_name: "",
        last_name: "",
        phone_number: "",
        email: "",
        password: "",
        isLoading: "",
      },
      msg: "",
    };
  }
  onChangehandler = (e, key) => {
    const { signupData } = this.state;
    signupData[e.target.name] = e.target.value;
    this.setState({ signupData });
  };
  onSubmitHandler = (e) => {
    e.preventDefault();
    this.setState({ isLoading: true });
    axios
      .post("http://localhost:8000/api/register", this.state.signupData)
      .then((response) => {
        this.setState({ isLoading: false })
        if (response.data.status === 200) {
          console.log(response)
          this.setState({
            msg: response.data.message,
            signupData: {
              first_name: "",
              last_name: "",
              phone_number: "",
              email: "",
              password: "",
              isLoading: "",
            },
          });
          setTimeout(() => {
            this.setState({ msg: "" });
          }, 2000);
        }

        if (response.data.status === "failed") {
          this.setState({ msg: response.data.message });
          setTimeout(() => {
            this.setState({ msg: "" });
          }, 2000);
        }
      });
  };




  render() {
    
      const isLoading = this.state.isLoading;

    return (
      <div class="hold-transition register-page">
        <div className="register-box">
          <div className="card card-outline card-primary">
            <div className="card-header text-center">
              <a href="../../index2.html" className="h1"><b>DBPMS</b></a>
            </div>
            <div className="card-body">
              <p className="login-box-msg">Register as client</p>
              <form action="../../index.html" method="post">
                <div className="input-group mb-3">
                  <input name="first_name" type="text" className="form-control" placeholder="First name" value={this.state.signupData.first_name}
                    onChange={this.onChangehandler} required />
                  <div className="input-group-append">
                    <div className="input-group-text">
                      <span className="fas fa-user" />
                    </div>
                  </div>
                </div>

                <div className="input-group mb-3">
                  <input name="last_name" type="text" className="form-control" placeholder="Last name"value={this.state.signupData.last_name}
              onChange={this.onChangehandler} required />
                  <div className="input-group-append">
                    <div className="input-group-text">
                      <span className="fas fa-user" />
                    </div>
                  </div>
                </div>
                <div className="input-group mb-3">
                  <input name="email" type="email" className="form-control" placeholder="Email" value={this.state.signupData.email}
              onChange={this.onChangehandler} required />
                  <div className="input-group-append">
                    <div className="input-group-text">
                      <span className="fas fa-envelope" />
                    </div>
                  </div>
                </div>
                <div className="input-group mb-3">
                  <input name="phone_number" type="text" className="form-control" placeholder="Phone number" value={this.state.signupData.phone_number}
              onChange={this.onChangehandler}required />
                  <div className="input-group-append">
                    <div className="input-group-text">
                      <span className="fas fa-phone" />
                    </div>
                  </div>
                </div>
                <div className="input-group mb-3">
                  <input name="password" type="password" className="form-control" placeholder="Password" value={this.state.signupData.password}
              onChange={this.onChangehandler} required/>
                  <div className="input-group-append">
                    <div className="input-group-text">
                      <span className="fas fa-lock" />
                    </div>
                  </div>
                </div>
                {/* <div className="input-group mb-3">
          <input type="password" className="form-control" placeholder="Retype password" />
          <div className="input-group-append">
            <div className="input-group-text">
              <span className="fas fa-lock" />
            </div>
          </div>
        </div> */}
                <div className="row">
                  <div className="col-8">

                  </div>
                  {/* /.col */}
                  <div className="col-4">
                    <button type="submit" className="btn btn-primary btn-block" onClick={this.onSubmitHandler}>Register
                    {isLoading ? (
              <span
                className="spinner-border spinner-border-sm ml-5"
                role="status"
                aria-hidden="true"
              ></span>
            ) : (
              <span></span>
            )}
                    
                    </button>
                  </div>
                  {/* /.col */}
                </div>
              </form>
              <a href="login.html" className="text-center">I already have an account</a>
            </div>
            {/* /.form-box */}
          </div>{/* /.card */}
        </div>

      </div>
    )
  }
}
