import React, { useState } from "react";
//import { Button, TextField, Card } from "@material-ui/core";
//import AccountCircleIcon from "@material-ui/icons/AccountCircle";
//import { useStyles } from "../../styles/styles";
//import { Link } from "react-router-dom";
//useSelector
import { useDispatch, } from 'react-redux';
import { RegisterAction } from '../../redux/actions/AuthActions';
function RegisterComponent() {
    //const classes = useStyles();
    const dispatch = useDispatch();
    //const authResponse = useSelector(state=>state.userAuth.authResponse);
    const [fields, setState] = useState({
        first_name: "",
        last_name: "",
        email: "",
        password: "",
        password_confirmation: "",
        phone_number: "",

    });
    const handleFieldChange = e => {
        setState({
            ...fields,
            [e.target.id]: e.target.value
        })
    }
    const UserRegister = (e) => {
        e.preventDefault();
        const passwordMatch = checkPasswordMatch(fields.password, fields.password_confirmation);
        if (passwordMatch === true) {
            alert('passwords dont match. please check your password again');
            return;
        }
        dispatch(RegisterAction(fields));
    };
    const checkPasswordMatch = (password, password_confirmation) => {
        return password !== password_confirmation ? true : false;
    }


    return (
      <div hold-transition register-page>
  <form onsubmit="{UserRegister}">
    <h3>Register</h3>
    <div classname="form-group">
      {'{'}/* <label>First name</label> */{'}'}
      <input type="text" name="first_name" id="first_name" onchange="{handleFieldChange}" defaultValue="{fields.first_name}" classname="form-control" placeholder="First name" required />
    </div>
    <div classname="form-group">
      {'{'}/* <label>Last name</label> */{'}'}
      <input type="text" name="last_name" id="last_name" onchange="{handleFieldChange}" defaultValue="{fields.last_name}" classname="form-control" placeholder="Last name" required />
    </div>
    <div classname="form-group">
      {'{'}/* <label>Phone Number</label> */{'}'}
      <input type="text" name="phone_number" id="phone_number" onchange="{handleFieldChange}" defaultValue="{fields.phone_number}" classname="form-control" placeholder="Phone number" required />
    </div>
    <div classname="form-group">
      {'{'}/* <label>Email address</label> */{'}'}
      <input type="text" name="email" id="email" onchange="{handleFieldChange}" defaultValue="{fields.email}" classname="form-control" placeholder="Email" required />
    </div>
    <div classname="form-group">
      {'{'}/* <label>Password</label> */{'}'}
      <input type="password" name="password" id="password" onchange="{handleFieldChange}" defaultValue="{fields.password}" classname="form-control" placeholder="Password" required />
    </div>
    <div classname="form-group">
      {'{'}/* <label>Password</label> */{'}'}
      <input type="password" name="password_confirmation" id="password_confirmation" onchange="{handleFieldChange}" defaultValue="{fields.password_confirmation}" classname="form-control" placeholder="password_confirmation" required />
    </div>
    <button type="submit" classname="btn btn-primary btn-block">Register</button>
    {'{'}/* <p classname="forgot-password text-right">
      Already registered <link to="/Login" />Login?
    </p> */{'}'}
  </form>
  <div className="register-box">
    <div className="card card-outline card-primary">
      <div className="card-header text-center">
        <a href="../../index2.html" className="h1"><b>Admin</b>LTE</a>
      </div>
      <div className="card-body">
        <p className="login-box-msg">Register a new membership</p>
        <form action="../../index.html" method="post">
          <div className="input-group mb-3">
            <input type="text" className="form-control" placeholder="Full name" />
            <div className="input-group-append">
              <div className="input-group-text">
                <span className="fas fa-user" />
              </div>
            </div>
          </div>
          <div className="input-group mb-3">
            <input type="email" className="form-control" placeholder="Email" />
            <div className="input-group-append">
              <div className="input-group-text">
                <span className="fas fa-envelope" />
              </div>
            </div>
          </div>
          <div className="input-group mb-3">
            <input type="password" className="form-control" placeholder="Password" />
            <div className="input-group-append">
              <div className="input-group-text">
                <span className="fas fa-lock" />
              </div>
            </div>
          </div>
          <div className="input-group mb-3">
            <input type="password" className="form-control" placeholder="Retype password" />
            <div className="input-group-append">
              <div className="input-group-text">
                <span className="fas fa-lock" />
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-8">
              <div className="icheck-primary">
                <input type="checkbox" id="agreeTerms" name="terms" defaultValue="agree" />
                <label htmlFor="agreeTerms">
                  I agree to the <a href="#">terms</a>
                </label>
              </div>
            </div>
            {/* /.col */}
            <div className="col-4">
              <button type="submit" className="btn btn-primary btn-block">Register</button>
            </div>
            {/* /.col */}
          </div>
        </form>
        <div className="social-auth-links text-center">
          <a href="#" className="btn btn-block btn-primary">
            <i className="fab fa-facebook mr-2" />
            Sign up using Facebook
          </a>
          <a href="#" className="btn btn-block btn-danger">
            <i className="fab fa-google-plus mr-2" />
            Sign up using Google+
          </a>
        </div>
        <a href="login.html" className="text-center">I already have a membership</a>
      </div>
      {/* /.form-box */}
    </div>{/* /.card */}
  </div>
</div>

    );

}
export default RegisterComponent;
