import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
class Register extends Component {
    state = {
        id: '',
        first_name: '',
        last_name: '',
        phone_number: '',
        email: '',
        password: '',

    }
    handleFirstNameInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value

        });
    }
    handleLastNameInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value

        });
    }
    handlePhoneInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value

        });
    }
    handleEmailInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value

        });
    }
    handlePasswordInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value

        });


    }
    saveApplicant = async (e) => {
        e.preventDefault();
        const res = await axios.post('/register', this.state);
        if (res.data.status === 200) {
            //console.log(res.data.message);
            this.setState({
                id: '',
                first_name: '',
                last_name: '',
                phone_number: '',
                email: '',
                password: '',

            });
        }
    }
    render() {
        return (
            <form onSubmit={this.saveApplicant}>
                <h3>Register</h3>

                <div className="form-group">
                    {/* <label>First name</label> */}
                    <input type='text' name='first_name' onChange={this.handleFirstNameInput} value={this.state.first_name} className='form-control' placeholder="First name" required />
                </div>

                <div className="form-group">
                    {/* <label>Last name</label> */}
                    <input type='text' name='second_name' onChange={this.handleLastNameInput} value={this.state.second_name} className='form-control' placeholder="Last name" required />
                </div>
                <div className="form-group">
                    {/* <label>Phone Number</label> */}
                    <input type='text' name='phone_number' onChange={this.handlePhoneInput} value={this.state.phone_number} className='form-control' placeholder='Phone number' required />
                </div>

                <div className="form-group">
                    {/* <label>Email address</label> */}
                    <input type='text' name='email' onChange={this.handleEmailInput} value={this.state.email} className='form-control' placeholder='Email' required />
                </div>

                <div className="form-group">
                    {/* <label>Password</label> */}
                    <input type='password' name='password' onChange={this.handlePasswordInput} value={this.state.password} className='form-control' placeholder='Password' required />
                </div>

                <button type="submit" className="btn btn-primary btn-block">Register</button>
                <p className="forgot-password text-right">
                    Already registered <Link to="/Login">Login?</Link>
                </p>
            </form>
        );
    }
}
export default Register;
