import React, { useState } from "react";
//import { Button, TextField, Card } from "@material-ui/core";
//import AccountCircleIcon from "@material-ui/icons/AccountCircle";
//import { useStyles } from "../../styles/styles";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux';
import { RegisterAction } from '../../redux/actions/AuthActions';
function RegisterComponent() {
    //const classes = useStyles();
    const dispatch = useDispatch();
    //const authResponse = useSelector(state=>state.userAuth.authResponse);
    const [fields, setState] = useState({
        name: "",
        email: "",
        password: "",
        password_confirmation: "",
    });
    const handleFieldChange = e => {
        setState({
            ...fields,
            [e.target.id]: e.target.value
        })
    }
    const UserRegister = (e) => {
        e.preventDefault();
        console.log(fields);
        const passwordMatch = checkPasswordMatch(fields.password, fields.password_confirmation);
        if (passwordMatch === true) {
            alert('passwords dont match. please check your password again');
            return;
        }
        dispatch(RegisterAction(fields));
    };
    const checkPasswordMatch = (password, password_confirmation) => {
        return password !== password_confirmation ? true : false;
    }


    return (
        <form onSubmit={UserRegister}>
            <h3>Register</h3>

            <div className="form-group">
                {/* <label>First name</label> */}
                <input type='text' name='first_name' onChange={handleFieldChange} value={state.first_name} className='form-control' placeholder="First name" required />
            </div>

            <div className="form-group">
                {/* <label>Last name</label> */}
                <input type='text' name='second_name' onChange={handleFieldChange} value={state.second_name} className='form-control' placeholder="Last name" required />
            </div>
            <div className="form-group">
                {/* <label>Phone Number</label> */}
                <input type='text' name='phone_number' onChange={handleFieldChange} value={state.phone_number} className='form-control' placeholder='Phone number' required />
            </div>

            <div className="form-group">
                {/* <label>Email address</label> */}
                <input type='text' name='email' onChange={handleFieldChange} value={state.email} className='form-control' placeholder='Email' required />
            </div>

            <div className="form-group">
                {/* <label>Password</label> */}
                <input type='password' name='password' onChange={handleFieldChange} value={state.password} className='form-control' placeholder='Password' required />
            </div>

            <button type="submit" className="btn btn-primary btn-block">Register</button>
            <p className="forgot-password text-right">
                Already registered <Link to="/Login">Login?</Link>
            </p>
        </form>
    );

}
export default RegisterComponent;
