import React, { useState } from "react";
//import { Button, TextField, Card } from "@material-ui/core";
//import AccountCircleIcon from "@material-ui/icons/AccountCircle";
//import { useStyles } from "../../styles/styles";
//import { Link } from "react-router-dom";
//useSelector
import { useDispatch, } from 'react-redux';
import { RegisterAction } from '../../redux/actions/AuthActions';
function RegisterComponent() {
    //const classes = useStyles();
    const dispatch = useDispatch();
    //const authResponse = useSelector(state=>state.userAuth.authResponse);
    const [fields, setState] = useState({
        first_name: "",
        last_name: "",
        email: "",
        password: "",
        password_confirmation: "",
        phone_number: "",

    });
    const handleFieldChange = e => {
        setState({
            ...fields,
            [e.target.id]: e.target.value
        })
    }
    const UserRegister = (e) => {
        e.preventDefault();
        const passwordMatch = checkPasswordMatch(fields.password, fields.password_confirmation);
        if (passwordMatch === true) {
            alert('passwords dont match. please check your password again');
            return;
        }
        dispatch(RegisterAction(fields));
    };
    const checkPasswordMatch = (password, password_confirmation) => {
        return password !== password_confirmation ? true : false;
    }


    return (
        // <form onSubmit={UserRegister}>
        //     <h3>Register</h3>

        //     <div className="form-group">
        //         {/* <label>First name</label> */}
        //         
        //     </div>

        //     <div className="form-group">
        //         {/* <label>Last name</label> */}
        //        
        //     </div>
        //     <div className="form-group">
        //         {/* <label>Phone Number</label> */}
        //      
        //     </div>

        //     <div className="form-group">
        //         {/* <label>Email address</label> */}
        //        
        //     </div>

        //     <div className="form-group">
        //         {/* <label>Password</label> */}
        //         <input type='password' name='password' id="password" onChange={handleFieldChange} value={fields.password} className='form-control' placeholder='Password' required />
        //     </div>
        //     <div className="form-group">
        //         {/* <label>Password</label> */}
        //        
        //     </div>

        //     <button type="submit" className="btn btn-primary btn-block">Register</button>
        //     {/* <p className="forgot-password text-right">
        //         Already registered <Link to="/Login">Login?</Link>
        //     </p> */}
        // </form>

        <div className="hold-transition register-page ">
            <div className="register-box">
                <div className="register-logo">
                    <a href="../../index2.html"><b>Addis Ababa Building permit management system </b></a>
                </div>
                <div className="card">
                    <div className="card-body register-card-body">
                        <p className="login-box-msg">Register a new membership</p>
                        <form onSubmit={UserRegister}>
                            <div className="input-group mb-3">
                                <input type='text' name='first_name' id="first_name" onChange={handleFieldChange} value={fields.first_name} className='form-control' placeholder="First name" required /><div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-user" />
                                    </div>
                                </div>
                            </div>
                            <div className="input-group mb-3"> 
                            <input type='text' name='last_name' id="last_name" onChange={handleFieldChange} value={fields.last_name} className='form-control' placeholder="Last name" required />    <div className="input-group-text">
                                        <span className="fas fa-user" />
                             </div>
                            </div>
                           
                            <div className="input-group mb-3">
                                   <input type='text' name='phone_number' id="phone_number" onChange={handleFieldChange} value={fields.phone_number} className='form-control' placeholder='Phone number' required /><div className="input-group-text">
                                        <span className="fas fa-user" />
                                    </div>
                                </div>
                            
                            <div className="input-group mb-3"> 
                            <input type='text' name='email' id="email" onChange={handleFieldChange} value={fields.email} className='form-control' placeholder='Email' required /> <div className="input-group-text">
                                        <span className="fas fa-user" />
                                    </div>
                                </div>
                                
                            <div className="input-group mb-3">
                                <input type='text' name='first_name' id="first_name" onChange={handleFieldChange} value={fields.first_name} className='form-control' placeholder="First name" required /><div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-user" />
                                    </div>
                                </div>
                            </div>
                            <div className="input-group mb-3">
                                <input type="email" className="form-control" placeholder="Email" />
                                <div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-envelope" />
                                    </div>
                                </div>
                            </div>
                            <div className="input-group mb-3">
                                <input type="password" className="form-control" placeholder="Password" />
                                <div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-lock" />
                                    </div>
                                </div>
                            </div>
                            <div className="input-group mb-3">
                                <input type="password" className="form-control" placeholder="Retype password" />
                                <div className="input-group-append">
                                    <div className="input-group-text">
                                        <span className="fas fa-lock" />
                                    </div>
                                </div>
                            </div>
                            <div className="row">
                                <div className="col-8">
                                    <div className="icheck-primary">
                                        <input type="checkbox" id="agreeTerms" name="terms" defaultValue="agree" />
                                        <label htmlFor="agreeTerms">
                                            I agree to the <a href="#">terms</a>
                                        </label>
                                    </div>
                                </div>
                                {/* /.col */}
                                <div className="col-4">
                                    <button type="submit" className="btn btn-primary btn-block">Register</button>
                                </div>
                                {/* /.col */}
                            </div>
                        </form>
                        <div className="social-auth-links text-center">
                            <p>- OR -</p>
                            <a href="#" className="btn btn-block btn-primary">
                                <i className="fab fa-facebook mr-2" />
                                Sign up using Facebook
                            </a>
                            <a href="#" className="btn btn-block btn-danger">
                                <i className="fab fa-google-plus mr-2" />
                                Sign up using Google+
                            </a>
                        </div>
                        <a href="login.html" className="text-center">I already have a membership</a>
                    </div>
                    {/* /.form-box */}
                </div>{/* /.card */}
            </div>
        </div>

    );

}
export default RegisterComponent;
